import React, { useState } from "react";
import { RxHamburgerMenu } from "react-icons/rx";
import { HashLink as Link } from "react-router-hash-link";
import { useAuth } from "../../contexts/AuthContextProvider";
import BurgerMenu from "./BurgerMenu";
import "./navbarStyle/navbar.css";

const Navbar = () => {
  const pages = [
    { name: "HOME", link: "/", id: 1 },
    { name: "NEWS", link: "#news", id: 2 },
    { name: "CHARACTERS", link: "/products", id: 3 },
    { name: "EXPLORE", link: "/aboutus", id: 4 },
  ];
  const [menuActive, setMenuActive] = useState(false);
  const items = [
    { value: "Home", href: "/" },
    { value: "News", href: "#" },
    { value: "Characters", href: "/products" },
    { value: "Explore", href: "/aboutus" },
    { value: "More", href: "/#" },
    { value: "Admin", href: "/admin" },
    { value: "Favorites", href: "/cart" },
  ];
  const { user, handleLogOut } = useAuth();
  return (
    <div className="navbar">
      <div className="navbar-logo">
        {/* <img
          src="https://uploadstatic-sea.mihoyo.com/hk4e/upload/officialsites/202004/GIEN_1587984203_6764.png"
          alt=""
        /> */}
      </div>
      <div className="burger-btn" onClick={() => setMenuActive(!menuActive)}>
        <span />
      </div>
      <ul className="navigation">
        {pages.map((page) => (
          <Link className="navigation__item" to={page.link} key={page.id}>
            <li className="navigation__item" sx={{ textDecoration: "none" }}>
              {page.name}
            </li>
          </Link>
        ))}
        {user.email === "admin@admin.com" ? (
          <Link className="navigation__item" to="/admin">
            ADMIN
          </Link>
        ) : (
          <li>
            <div>
              <Link className="navigation__item" to="/cart">
                FAVOURITES
              </Link>
              <Link className="navigation__item" to="/payform">
                DONATE
              </Link>
            </div>
          </li>
        )}
      </ul>
      <div className="navbar-login">
        {user.email ? (
          <button className="login__btn">
            <span onClick={handleLogOut}>Log Out</span>
            <img
              src="data:image/png;base64,"
              alt=""
            />
          </button>
        ) : (
          <button className="login__btn">
            <Link to="/auth">
              {" "}
              <span style={{ color: "white" }}>Log In</span>
            </Link>
            <img
              src="data:image/png;base64,"
              alt=""
            />
          </button>
        )}
        <div className="navbar__btn"></div>
      </div>
      <BurgerMenu
        active={menuActive}
        setActive={setMenuActive}
        header={"Menu"}
        items={items}
      />
    </div>
  );
};

export default Navbar;
